package basicPrograms;

public class StringsPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String a="Rakeh Reddy Chityala";
		String b="Rakesh Reddy Chityala";
		String c=new String("Rakesh Reddy Chityala");
		String d=new String("Rakesh Reddy Chityala");
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(a.charAt(2));
		System.out.println(a.indexOf("e"));
		System.out.println(a.substring(6, 12));
		System.out.println(a.substring(12));
		
	}

}
